mapper.select:
SELECT COUNT(id) AS USER_CNT
FROM   USER
WHERE  email = #{email}

SELECT COUNT(id) AS USER_CNT
FROM   USER
WHERE  nickname = #{nickname}

mapper.insert:
INSERT INTO USER (
       id
     , role
     , status
     , email
     , password
     , name
     , nickname
     , phone
     , rtk
     , last_login_at
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{role}
     , #{status}
     , #{email}
     , #{password}
     , #{name}
     , #{nickname}
     , #{phone}
     , null
     , null
     , CURRENT_TIMESTAMP
     , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE USER
SET
       rtk           = #{rtk}
     , last_login_at = CURRENT_TIMESTAMP
     , updated_at    = CURRENT_TIMESTAMP
WHERE  id = #{user.id}

mapper.insert:
INSERT INTO USER_LOGIN_HISTORY (
       id
     , user_id
     , ip_address
     , user_agent
     , device_id
     , login_at
)
VALUES (
       #{id}
     , #{userId}
     , #{ipAddress}
     , #{userAgent}
     , #{deviceId}
     , CURRENT_TIMESTAMP
)
