mapper.select:
SELECT COUNT(id) AS USER_CNT
FROM   USER
WHERE  email = #{email}

SELECT COUNT(id) AS USER_CNT
FROM   USER
WHERE  nickname = #{nickname}

mapper.insert:
INSERT INTO USER (
       id
     , role
     , status
     , email
     , password
     , name
     , nickname
     , phone
     , rtk
     , last_login_at
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{role}
     , #{status}
     , #{email}
     , #{password}
     , #{name}
     , #{nickname}
     , #{phone}
     , null
     , null
     , CURRENT_TIMESTAMP
     , CURRENT_TIMESTAMP
)
